var open=document.querySelector(".write-us"),popup=document.querySelector(".modal"),close=document.querySelector(".modal-close"),form=popup.querySelector("form"),username=form.querySelector("[name=name]"),email=form.querySelector("[name=email]");open.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),username.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),form.addEventListener("submit",function(e){username.value&&email.value||(e.preventDefault(),popup.classList.add("modal-error"))});